public boolean validateWrite()
{
    boolean ret;
    ret = next validateWrite();

    // Carrega o parâmetro UMA vez
    CustParameters custParameters = CustParameters::find();

    if (ret) 
    {   
        // Usa a variável carregada
        if (this.CreditMax > custParameters.ABCCreditLimitThreshold 
            && custParameters.ABCAtivarValidacaoVIP == NoYes::Yes) 
        {
            // Usa a variável aqui também
            ret = checkFailed(strFmt("@EtiquetasMensagens:ErroLimiteExcedido", custParameters.ABCCreditLimitThreshold));
        }
    }
    return ret;
}
