}

Method.Source:
// Método 1: Grupo de Clientes (Com atributos)
[
    DataMemberAttribute,
    SysOperationLabelAttribute(literalStr("Grupo de Clientes")),
    SysOperationHelpTextAttribute(literalStr("Selecione o grupo para atualizar"))
]
public CustGroupId parmCustGroupId(CustGroupId _custGroupId = custGroupId)
{
    custGroupId = _custGroupId;
    return custGroupId;
}

// Método 2: Validação (SEM atributos de tela)
public boolean validate()
{
    boolean ret = true;
    if (creditMax <= 0)
    {
        ret = false;
        checkFailed(strFmt("@EtiquetasMensagens:ErroLimiteZero"));
    }
    return ret;
}

// Método 3: Novo Limite (Os atributos vêm para cá!)
[
    DataMemberAttribute,
    SysOperationLabelAttribute(literalStr("Novo Limite")),
    SysOperationHelpTextAttribute(literalStr("Informe o novo valor máximo de crédito"))
]
public Amount parmCreditMax(Amount _creditMax = creditMax)
{
    creditMax = _creditMax;
    return creditMax;
}
