// O ponto de entrada (o botão de Play)
public static void main(Args _args)
{
    JobAtualizaVIPController controller;

    // Aqui fazemos a mágica: Dizemos quem é o MOTOR (Classe) e quem é a ENGRENAGEM (Método)
    controller = new JobAtualizaVIPController(
        classStr(JobAtualizaVIPService),
        methodStr(JobAtualizaVIPService, processar),
        SysOperationExecutionMode::ScheduledBatch);

    // Inicia o processo (desenha a tela e aguarda o OK)
    controller.startOperation();
}

